sys_variable_value.value:
(function execute(inputs, outputs) {
// ... Scopped App code ...
    var currRec = inputs.currentrecord;
  var jiraField ={}; 
  //should be in this order environment,number,uri,assignment_group,service
  var additionalFields = gs.getProperty('nhs.jira.additional_fields').split(',');

  jiraField.environment= additionalFields[0];
  jiraField.number = additionalFields[1];
  jiraField.uri =  additionalFields[2];
  jiraField.assignment_group =  additionalFields[3];
  jiraField.service =  additionalFields[4];

  var jiraValue={};
  jiraValue.environment = gs.getProperty('instance_name');
  jiraValue.number = currRec.number.toString();
  var uri = "https://";
  uri=uri+jiraValue.environment;
  uri=uri+".service-now.com/task.do?sysparm_query=number=";
  uri=uri+jiraValue.number;
  jiraValue.uri= uri;
  jiraValue.assignment_group = currRec.assignment_group.name.toString();
  jiraValue.service = currRec.business_service.name.toString();
  var jiraKeyValue =[];
  for(key in jiraField){
    jiraKeyValue.push(jiraField[key]+'='+jiraValue[key]);
  }
  outputs.additionalfields= jiraKeyValue.join('^');
})(inputs, outputs);

