sys_variable_value.value:
(function execute(inputs, outputs) {
 // ... App Code ...
  var payload = JSON.parse(inputs.responsePayload);
  var issueType = payload.issue.fields.issuetype.name;
  var tableName = inputs.taskRecord.sys_class_name;
  var record = inputs.taskRecord.number;
  var dateTime = new GlideDateTime().getDisplayValue();
  var statusProp = gs.getProperty('x_nhsd_nhs_jira_in.nhs.jira.status.values');
  var gr = new GlideRecord(tableName);
  gr.addQuery("x_nhsd_nhs_jira_in_issue_id",payload.issue.id);
  gr.query();
  while(gr.next()){ 

    if((tableName == 'incident_task') && (statusProp.indexOf(payload.issue.fields.status.name) !=-1)){
      gr.u_resolution_notes = 'Jira Issue key '+gr.x_nhsd_nhs_jira_in_issue_key+' is marked Done at '+dateTime+' by '+payload.user.displayName;
      gr.work_notes=inputs.taskRecord.number+' resolved/closed automatically.';
      gr.state = 5; 

    }
    else if((tableName == 'problem_task') && (statusProp.indexOf(payload.issue.fields.status.name) !=-1)){
      gr.close_notes = 'Jira Issue key '+gr.x_nhsd_nhs_jira_in_issue_key+' is marked Done at '+dateTime+' by '+payload.user.displayName;
      gr.work_notes=inputs.taskRecord.number+' resolved/closed automatically.';
      gr.needs_attention = true;
      gr.state = 157; 
      var gr1 = new GlideRecord('problem');
      gr1.get(gr.problem);
      gr1.needs_attention = true;
      gr1.update();

    }
    else if((tableName == 'sc_task') && (statusProp.indexOf(payload.issue.fields.status.name) !=-1)){

      gr.u_resolution_code = '1';
      gr.needs_attention = true;
      gr.work_notes = inputs.taskRecord.number+' resolved/closed automatically.';
      gr.u_resolution_notes = 'Jira Issue key '+gr.x_nhsd_nhs_jira_in_issue_key+' is marked Done at '+dateTime+' by '+payload.user.displayName;
      gr.state = 5;
      var gr1 = new GlideRecord('sc_req_item');
      gr1.get(gr.request_item);
      gr1.needs_attention = true;
      gr1.update();

    }

    gr.x_nhsd_nhs_jira_in_issue_status = payload.issue.fields.status.name;
    var newProjectKey = payload.issue.key.toString();
    //Update Issue Key and Issue URL
    if(newProjectKey){ 
      var url = gr.x_nhsd_nhs_jira_in_issue_url.toString();
      var issueKey = gr.x_nhsd_nhs_jira_in_issue_key.toString();
      var newURL = url.replaceAll(issueKey,newProjectKey);
      gr.x_nhsd_nhs_jira_in_issue_url = newURL;
    }
    //Update Worklog and comments
    var cmt="";
    var hasComment = payload.hasOwnProperty("comment");
    if(hasComment && hasComment != undefined){    
      cmt = payload.comment.body;
      if(cmt.indexOf("ServiceNow Worknotes") == -1){
        var userName = payload.comment.author.displayName;
        gr.work_notes = "Jira comment added by " + userName + " - " + cmt;
      }
    }
    var projectName = payload.issue.fields.project.name;
    var oldprojectKey = gr.x_nhsd_nhs_jira_in_issue_key;
    var oldprojectId = oldprojectKey.split('-');

    var gr1 = new GlideRecord('u_nhs_data_lookup');
    gr1.addEncodedQuery('u_key='+oldprojectId[0]);
    gr1.query();
    if(gr1.next()){
      var oldProjectName = gr1.u_name;
    }

    if(projectName != oldProjectName){//for projectName         
      gr.work_notes = " JIRA Project Changed :: "+projectName +" was "+oldProjectName;
    }

    var journalEnteries = new GlideRecord('sys_journal_field'); journalEnteries.addEncodedQuery('name='+tableName+'^element=work_notes^element_id='+inputs.taskRecord.sys_id+'^valueSTARTSWITHIssue Type has been changed in Jira to');
    journalEnteries.orderByDesc('sys_created_on');
    journalEnteries.query();

    if(journalEnteries.next()){
      var worknote = journalEnteries.value;
      var oldIssueType = worknote.substring(42, worknote.length);

      if(oldIssueType != '' && oldIssueType != issueType){
        gr.work_notes = 'Issue Type has been changed in Jira to :: '+issueType;
      }
    }
    else{
      var table = '';
      if(tableName == 'incident' || tableName == 'incident_task')
        table = 'incident';
      else if(tableName == 'problem' || tableName == 'problem_task')
        table = 'problem';
      else if(tableName == 'sc_req_item' || tableName == 'sn_customerservice_case' || tableName == 'sc_task')
        table = 'request';
      if(issueType.toLowerCase().indexOf(table) < 0)
        gr.work_notes = 'Issue Type has been changed in Jira to :: '+issueType;
    }
    gr.x_nhsd_nhs_jira_in_issue_key = payload.issue.key;
    gr.update();

  }

})(inputs, outputs);

