sys_variable_value.value:
(function execute(inputs, outputs) {
  var sysID = inputs.taskRecord;
 var cmnt = new GlideRecord('sys_journal_field');
cmnt.addQuery('element_id',sysID);//current.sys_id;
cmnt.orderByDesc('sys_created_on');
cmnt.query();
if(cmnt.next())
{
    var comment = cmnt.value;
        outputs.comment= 'ServiceNow Worknotes: '+comment;

    }

// ... code ...
})(inputs, outputs);

