sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
		return;
	}else if(newValue === ''){
		g_form.clearValue('x_nhsd_nhs_jira_in_issue_id');
		g_form.clearValue('x_nhsd_nhs_jira_in_issue_key');
		g_form.clearValue('x_nhsd_nhs_jira_in_issue_status');
		g_form.clearValue('x_nhsd_nhs_jira_in_issue_url');
		g_form.clearValue('x_nhsd_nhs_jira_in_jira_project');
	}
	else {
		g_form.clearValue('x_nhsd_nhs_jira_in_issue_id');
		g_form.clearValue('x_nhsd_nhs_jira_in_issue_key');
		g_form.clearValue('x_nhsd_nhs_jira_in_issue_status');
		g_form.clearValue('x_nhsd_nhs_jira_in_issue_url');
		g_form.clearValue('x_nhsd_nhs_jira_in_jira_project');

		var issueObject = g_form.getReference('x_nhsd_nhs_jira_in_search_issue',getDetail);
	}
	g_form.clearValue('x_nhsd_nhs_jira_in_reset_jira_information');
}
function getDetail(issueObject){
	g_form.setValue('x_nhsd_nhs_jira_in_issue_id', issueObject.x_nhsd_nhs_jira_in_issue_id);
	g_form.setValue('x_nhsd_nhs_jira_in_issue_key', issueObject.x_nhsd_nhs_jira_in_issue_key);
	g_form.setValue('x_nhsd_nhs_jira_in_issue_status', issueObject.x_nhsd_nhs_jira_in_issue_status);
	g_form.setValue('x_nhsd_nhs_jira_in_issue_url', issueObject.x_nhsd_nhs_jira_in_issue_url);

	if (issueObject.x_nhsd_nhs_jira_in_jira_project == '') {
		var IncIssuekey = issueObject.x_nhsd_nhs_jira_in_issue_key;
		var IncIssueId = IncIssuekey.split('-');

		var gr = new GlideAjax('x_nhsd_nhs_jira_in.getDefaultJIRAProjectScope');
		gr.addParam('sysparm_name','getDefaultJIRATaskProject');
		gr.addParam('sysparm_issueKey', IncIssueId[0]);
		gr.getXMLAnswer(_handleResponse);

		function _handleResponse(response) { 
			var answer = response;
			g_form.setValue('x_nhsd_nhs_jira_in_jira_project', answer.toString());
		}
	}else{
		g_form.setValue('x_nhsd_nhs_jira_in_jira_project', issueObject.x_nhsd_nhs_jira_in_jira_project);
	}
}
