function onSubmit() {
   //Type appropriate comment here, and begin script below

	if((g_form.getValue('x_nhsd_nhs_jira_in_search_issue') === '' && g_form.getValue('x_nhsd_nhs_jira_in_create_issue') == "false") || (g_form.getValue('x_nhsd_nhs_jira_in_issue_id') == '' && g_form.getValue('x_nhsd_nhs_jira_in_issue_key') == '' && g_form.getValue('x_nhsd_nhs_jira_in_issue_status') == '' && g_form.getValue('x_nhsd_nhs_jira_in_issue_url') == '')){
		g_form.setValue('x_nhsd_nhs_jira_in_create_issue',false);
		g_form.setValue('x_nhsd_nhs_jira_in_show_jira_tab',false);
	}

}
