sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below

    if (g_form.getValue('x_nhsd_nhs_jira_in_show_jira_tab') == 'true' && g_form.getValue('x_nhsd_nhs_jira_in_create_issue') == 'false' && g_form.getValue('x_nhsd_nhs_jira_in_issue_status') != "Cancel") {

        g_form.setVisible('x_nhsd_nhs_jira_in_search_issue', true);
        g_form.setMandatory('x_nhsd_nhs_jira_in_search_issue', true);

    }
	else{
		g_form.setDisplay('x_nhsd_nhs_jira_in_search_issue',false);
		//g_form.setVisible('x_nhsd_nhs_jira_in_search_issue', false);

	}

}
