sys_ui_action.script:
function showPopupTask() {
    if ((g_form.getValue('x_nhsd_nhs_jira_in_create_issue') == 'true') && (g_form.getValue('x_nhsd_nhs_jira_in_jira_project') != '')) {
        alert("There is already a JIRA issue created for this record.");
        return false;
    } else {
        var ajax = new GlideAjax('x_nhsd_nhs_jira_in.getDefaultJIRAProjectScope');
        ajax.addParam('sysparm_name', 'getDefaultJIRATaskProject');
        ajax.addParam('sysparm_tablename', g_form.getTableName());
        ajax.addParam('sysparm_sysID', g_form.getUniqueValue());
        ajax.getXML(prepareMessage);
    }
}

function prepareMessage(response) {
    var fieldString = response.responseXML.documentElement.getAttribute("answer");
    var fields = fieldString.split(',');
    var gDialog = new GlideModal('x_nhsd_nhs_jira_in_JIRA_Project_Scoped_Dialog_Box',true);
    gDialog.setTitle(new GwtMessage().getMessage('Select JIRA Project'));
    gDialog.setSize(370,300);
        gDialog.setPreference('sysparm_sysID', g_form.getUniqueValue());

    if ((fields[0] != "" || fields[0] != "undefined") && (fields[1] != "" || fields[1] != "undefined")) {
        gDialog.setPreference('sysparm_table', g_form.getTableName());
        gDialog.setPreference('sysparm_project', fields[0]);
        gDialog.setPreference('sysparm_projectID', fields[1]);
    }
	gDialog.render();
}
